    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        String ID = "\n"+bID.getText()  + " /";
        String Name = bName.getText()+" /";
        String Author = bAuthor.getText()+" /";
        String Copie = spin.getValue().toString();

        File file = new File("Data/Resurve.txt");
        try {
            if (file.exists() == false) {
                file.createNewFile();
            }
            PrintWriter out = new PrintWriter(new FileWriter(file, true));
            out.append(ID);
            out.append(Name);
            out.append(Author);
            out.append(Copie + "\r");
            out.close();

            JOptionPane.showMessageDialog(null, "Book Updated!");

        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Invalid file creation");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed

        String filepath = "Data/Resurve.txt";
        File file = new File(filepath);
        try {
            BufferedReader br = new BufferedReader(new FileReader(file));
            String firstLine = br.readLine().trim();
            String[] columnName = firstLine.split(",");
            DefaultTableModel model = (DefaultTableModel) jTable1.getModel();
            model.setColumnIdentifiers(columnName);
            Object[] tableLines = br.lines().toArray();

            for (int i = 0; i < tableLines.length; i++) {
                String line = tableLines[i].toString().trim();
                String[] dataRow = line.split("/");
                model.addRow(dataRow);

            }

        } catch (Exception e) {
            System.out.println(e);
        }


    }//GEN-LAST:event_jButton3ActionPerformed

